angular.module("todoApp",[]).controller("TodoListController",function(){var a=this;a.todos=[{text:"learn angular",done:true},{text:"build an angular app",done:false}];a.addTodo=function(){a.todos.push({text:a.todoText,done:false});a.todoText=""};a.count=function(){var b=0;angular.forEach(a.todos,function(c){b+=c.done?0:1});return b};a.clear=function(){var b=a.todos;a.todos=[];angular.forEach(b,function(c){if(!c.done){a.todos.push(c)}})}});